import styled from 'styled-components';

export const Container = styled.div`


  background: #009999;
  padding: 0 30px;
  border-radius: 4px;

  div {
    display: flex;
    align-items: center;

  }

  a {
    margin-left: 10px;
    color: #fff;
    text-decoration: none;
  }

`;


export const Content = styled.div`
  height: 64px;
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;



`;



export const Profile = styled.header`

  display: flex;
  margin-left: 20px;
  padding-left: 20px;
  border-left: 1px solid #eee;

  div{

  text-align: right;
  margin-right: 10px;


  strong {
    font-size: 20px;
    display: block;
    color: rgba(0,0,0,0.0300);
  }

  a {
    display: block;
    margin-top: 2px;
    font-size: 15px;
    color: #FFF;
  }

  }

`;
