import storage from 'redux-persist/lib/storage';

import { persistReducers } from 'redux-persist';

export default reducers => {
  const persistReducers = persistReducer(
    {
    key: 'myshelf',
    storage,
    whitelist: ['auth', 'user'],
    },
    reducers
  );

  return persistReducers;
}
