import styled from 'styled-components';
import { Link } from 'react-router-dom';

import { darken } from 'polished';

export const StyledLink = styled(Link)`
text-decoration: none;
display: flex;
align-items: center;
color: #fff;
`;

export const BookList = styled.ul`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 20px;
  list-style: none;

li {
  display: flex;
  flex-direction: column;
  background: rgba(0,0,0,0.0300);
  border-radius: 4px;
  padding: 20px;

img {

  margin-bottom: 5px;
  align-self: center;
  max-width: 250px;
  width: 175px;
  height: 260px;
}

a {
  align-self: center;
}

   strong {
    font-size: 16px;
    line-height: 20px;
    color: #333;
    margin-bottom: 20px;
    display: flex;
    justify-content: center

}

  > span {
    flex: 1;
    text-align: center;
    font-size: 21px;
    font-weight: bold;
    margin: 5px 0 20px;
  }

  div {
  display: grid;
  }
}

`;


