import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.header`

  align-items: center;
  display: flex;
  justify-content: center;
  margin: 40px 0;
  text-decoration: none;
  font-size: 30px;

  div {
    display: flex;
    align-items: center;

  }

  a {
    margin-left: 10px;
    color: #009999;
    text-decoration: none;
  }

`;
