import styled from 'styled-components';
import { darken } from 'polished';

export const Wrapper = styled.div`
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(0,0,0,0.06);
  border: 0;
  border-radius: 4px;
  width: 400px;
  margin: auto;

`;

export const Content = styled.div`
  width: 100%;
  max-width: 315px;
  text-align: center;


  h1 {
      margin-top: 25px;
      display: flex;
      justify-content: center;
      font-size: 25px;
      display: flex;
      flex-direction: row;
      color: #009999;
      margin-bottom: 20px;
    }




  }



`;
